import React, { useCallback, useEffect, useReducer, useRef, useState }from 'react';
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button'
import TextDetection from './Containers/TextDetection/TextDetection';
import { green } from '@material-ui/core/colors'
import { BottomNavigation } from '@material-ui/core';
import LinearProgress from '@material-ui/core/LinearProgress';
import Snackbar from '@material-ui/core/Snackbar';

import { createMuiTheme , ThemeProvider } from '@material-ui/core/styles'
import {
  orange,
  lightBlue,
  deepPurple,
  deepOrange
} from "@material-ui/core/colors";
import POList from './Components/Tables/POList';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    minHeight: '100vh',
    flexDirection: 'column',
    // justifyContent: 'space-between',
  },
  mainArea:{
    display: 'flex',
    flexDirection: 'column',
    marginTop: 35,
    // height: 'calc(100vh-35px)',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
    display: 'flex',
    justifyContent: 'center',
  },
  lowerArea: {
    display: 'flex',
    justifyContent: 'center'
  },
  progress: {
    width: '100%',
    '& > * + *': {
      marginTop: theme.spacing(2),
    }
  },
  footer: {
    width: '100%',
    position: 'fixed',
    bottom: 0,
  }
}))

export default function App() {
  const [darkState, setDarkState ] = useState(false);
  const [invoices, setInvoices] = useState([]);
  const [open, setOpen ] = useState() 
  const [json, setJson] = useState({})
  const [searchStatus, setStatus] = useState("fulfilled")
  const snackText = useRef()

  let invoiceList = invoices.length

  useEffect(() => {
    console.log(json)
  }, [json])

  const classes = useStyles()

  const palletType = darkState ? "dark" : "light";
  const mainPrimaryColor = darkState ? orange[500] : lightBlue[500];
  const mainSecondaryColor = darkState ? deepOrange[900] : deepPurple[500];

  const darkTheme = createMuiTheme({
    palette: {
      type: palletType,
    },
    primary: {
      main: mainPrimaryColor
    },
    secondary: {
      main: mainSecondaryColor
    },
    sendToBP: {
      palette: green
    }
  });

  const handleThemeChange = () => {
    setDarkState(!darkState)
  };

  function clearText() {
    // setInvoices([{
    //   name: invoices[0].originalname || '',
    //   purchaseOrders: [],
    //   validNumber: '',
    //   date: '',
    // }])
    setInvoices([])
  }

  async function searchBP (orderNumber) {
    const findOrder = await fetch('/queryBp', {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({orderId: orderNumber})
    })
    const foundOrder = await findOrder.json()
    try {
      if (foundOrder !== null) {
        return foundOrder
      } else {
        throw new Error(`Could not find matching order for ref ${orderNumber}`)
      }
    }
    catch (err) {
      console.log(err)
    }
  }

  // async function checkDB (invoiceNumber) {
  //   const checkInvoice = await fetch('/queryInvoice', {
  //     method: "post",
  //     headers: {
  //       'Content-Type': 'application/json'
  //     },
  //     body: JSON.stringify({validNumber: invoiceNumber})
  //   })
  //   const checkComplete = await checkInvoice.json()
  //   try {
  //     console.log(checkComplete[0])
  //     return checkComplete[0]
  //   } catch (err) {
  //     console.log(err)
  //     handleOpen(true, 'Some documents do not appear to have a PO number.')
  //   }
  // }

  //above function wrapped in a useCallback hook
  const checkDB = useCallback(
    async (name, invoiceNumber) => {
      const checkInvoice = await fetch('/queryInvoice', {
        method: "post",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({validNumber: invoiceNumber})
      })
      const checkComplete = await checkInvoice.json()
      try {
        if (invoiceNumber) {
          return checkComplete[0]
        } else {
          throw new Error(`${name} has no valid PO number`)
        }
      } catch (err) {
        console.log(err)
      }
    },
    []
  )

  const handleOpen = (isOpen, response) => {
    snackText.current = response
    setOpen(isOpen)
  }
  const handleClose = () => {
    setOpen(false)
  }

  const writeToFile = () => {
    invoices.forEach(async (invoice) => {
      let matchedNumbers = await checkDB(invoice.name, invoice.validNumber)
      if (!matchedNumbers && invoice.validNumber) {
        const response = await fetch('http://localhost:5000/writeInvoice', {
          method: "post",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(invoice)
        })
        const body = await response.json()
        invoice.updated = true
        return body
      }
    })
    let checkAllUpdated = invoices.every((invoice) => {
      return invoice.updated === true
    })
    console.log(checkAllUpdated, invoices)
    if (checkAllUpdated) {
      handleOpen(true, `All documents updated successfully.`)
    } else {
      handleOpen(true, `Some records could not be updated.`)
    }
  }

  // useEffect(() => {
  //   invoices.forEach(async (invoice, index) => {
  //     let matchedNumber = await checkDB(invoice, invoice.validNumber)
  //     console.log(index, matchedNumber)
  //     if (matchedNumber) {
  //         let newArr = [...invoices]
  //         newArr[index].updated = true
  //         setInvoices(newArr)
  //       }
  //     }
  //   )

  // },[invoices, checkDB])

  //lifecycle event checked each candidate PO number against BP to find a matching PO
  //only updates when the invoices.length changes
  // useEffect(() => {
  //   if(invoiceList > 0) {
  //     invoices.forEach((invoice, index) => {
  //       invoice.purchaseOrders.forEach((purchaseOrder) => {
  //         searchBP(purchaseOrder).then(response => {
  //           if (response) {
  //             let newArr = [...invoices]
  //             console.log(newArr)
  //             newArr[index].validNumber = response.id
  //             setInvoices(newArr)
  //           }
  //         }).catch(err => console.log(err))
  //       })
  //     })
  //   }
  // },[invoiceList])

  //changes the above hook to useReducer
  const reducer = (state, action) => {
    switch(action.type) {
      case "searchBP": {
        const validatedArr = action.data.map((invoice, index) => {
          invoice.purchaseOrders.forEach(async (purchaseOrder) => {
            let isValidPO = await searchBP(purchaseOrder)
            if(isValidPO) {
              invoice.validNumber = isValidPO.id
            } else {
              handleOpen(true, 'Some documents do not appear to have a PO number.')
            }
          })
          return invoice
        })
        console.log(validatedArr)
        // validatedArr.forEach(async (invoice) => {
        //   let checked = await checkDB(invoice, invoice.validNumber)
        //   console.log(checked)
        // })
        setInvoices(validatedArr)
      }
      default:
        return state;
    }
  }

  const [state, dispatch] = useReducer(reducer, [])

  return (
    <ThemeProvider theme={darkTheme}>
      <div className={classes.root}>
        <Grid>
          <TextDetection 
            keyDataToParent={dispatch} 
            clearText={clearText}
            handleOpen={handleOpen}
            setStatus={setStatus}
          />
        </Grid>
        <main className={classes.mainArea}>
          <Grid container spacing={3} className={classes.container}>
            <Grid item sm={1}/>
            <Grid item sm={10}>
              <Grid container >
                <POList 
                  invoices={invoices}
                  setJson={setJson}
                  searchBP={searchBP}
                  setInvoices={setInvoices}
                  checkDB={checkDB}
                />
              </Grid>
            </Grid>
            <Grid item sm={1}/>
          </Grid>
          <Grid item sm={12} className={classes.lowerArea}>
            <Snackbar 
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'center'
              }}
              open={open}
              autoHideDuration={6000}
              onClose={handleClose}
              message={snackText.current}
            >
            </ Snackbar>
            <Button variant="contained" type="button" disableElevation onClick={() => writeToFile()}>Send to BrightPearl</Button>
          </Grid>
        </main>
        <Grid item sm={12} className={classes.footer} >
          <BottomNavigation>
            {searchStatus === "pending" ? 
              <div className={classes.progress}>
                <LinearProgress /> 
              </div>
              : 
              null 
            }
          </BottomNavigation>
        </Grid>
      </div>
    </ThemeProvider>
  );
}
